import re
import time
def main(arg1) -> dict:
    urls = re.findall(r'http[s]?://[^\s)]+', arg1)
    return {
        "result": urls,
    }

import time
def main(arg1) -> dict:
    result = []
    for item in arg1:
        url, text = item.split('\\SP')
        text = text.replace('\n', ' ')
        result.append({'url':url, 'text':text})
    return {
        "result": result,
    }

def main(depth: int) -> dict:
    depth = depth or 3
    array = list(range(depth))
    return {
        "array": array,
        "depth": depth
    }
